mapper.select:
select t.patient_id, t.name, t.sex_code, t.birth_date, t.id_type_code, t.id_no, t.tel_no, t.contact_name, t.contact_tel_no, t.marriage_code, t.education_code, t.employer_name, t.occupation_code, t.org_code, t.card_type_code, t.card_no, t.health_record_no, t.nationality_code, t.residence_mark, t.abo_code, t.rh_code, t.drug_allergy_mark, t.op_history_mark, t.trauma_history_mark, t.blood_transf_mark, t.disability_mark, t.genetic_disease_history, t.operation_history, t.last_update_dtime from baseinfo t
order by
${paramMap.orderCol}
patient_id desc
limit #{paramMap.pageOffset},#{paramMap.pageSize}

select

from baseinfo t 
where t.patient_id = #{patientId,jdbcType=BIGINT}

mapper.update:
update baseinfo
patient_id=#{patientId,jdbcType=BIGINT}, name=#{name,jdbcType=VARCHAR}, sex_code=#{sexCode,jdbcType=VARCHAR}, birth_date=#{birthDate,jdbcType=TIMESTAMP}, id_type_code=#{idTypeCode,jdbcType=VARCHAR}, id_no=#{idNo,jdbcType=VARCHAR}, tel_no=#{telNo,jdbcType=VARCHAR}, contact_name=#{contactName,jdbcType=VARCHAR}, contact_tel_no=#{contactTelNo,jdbcType=VARCHAR}, marriage_code=#{marriageCode,jdbcType=VARCHAR}, education_code=#{educationCode,jdbcType=VARCHAR}, employer_name=#{employerName,jdbcType=VARCHAR}, occupation_code=#{occupationCode,jdbcType=VARCHAR}, org_code=#{orgCode,jdbcType=VARCHAR}, card_type_code=#{cardTypeCode,jdbcType=VARCHAR}, card_no=#{cardNo,jdbcType=VARCHAR}, health_record_no=#{healthRecordNo,jdbcType=VARCHAR}, nationality_code=#{nationalityCode,jdbcType=VARCHAR}, residence_mark=#{residenceMark,jdbcType=CHAR}, abo_code=#{aboCode,jdbcType=VARCHAR}, rh_code=#{rhCode,jdbcType=VARCHAR}, drug_allergy_mark=#{drugAllergyMark,jdbcType=CHAR}, op_history_mark=#{opHistoryMark,jdbcType=CHAR}, trauma_history_mark=#{traumaHistoryMark,jdbcType=CHAR}, blood_transf_mark=#{bloodTransfMark,jdbcType=CHAR}, disability_mark=#{disabilityMark,jdbcType=CHAR}, genetic_disease_history=#{geneticDiseaseHistory,jdbcType=VARCHAR}, operation_history=#{operationHistory,jdbcType=VARCHAR}, last_update_dtime=#{lastUpdateDtime,jdbcType=TIMESTAMP},
where 1=1 and patient_id = #{patientId,jdbcType=BIGINT}
