mapper.select:
select t.section, t.name, t.code, t.description, t.code_notes from codetable t
order by
${paramMap.orderCol}
code,name,section desc
limit #{paramMap.pageOffset},#{paramMap.pageSize}

mapper.delete:
delete from codetable where 1=1 
and code=#{code,jdbcType=VARCHAR}
and name=#{name,jdbcType=VARCHAR}
and section=#{section,jdbcType=VARCHAR}

mapper.update:
update codetable
section=#{section,jdbcType=VARCHAR}, name=#{name,jdbcType=VARCHAR}, code=#{code,jdbcType=VARCHAR}, description=#{description,jdbcType=VARCHAR}, code_notes=#{codeNotes,jdbcType=VARCHAR},
where 1=1 and code = #{code,jdbcType=VARCHAR} and name = #{name,jdbcType=VARCHAR} and section = #{section,jdbcType=VARCHAR}
