mapper.select:
select t.id, t.name, t.parent_id, t.icon_cls, t.add_time, t.remark from goods_category t
order by
${paramMap.orderCol}
id desc
limit #{paramMap.pageOffset},#{paramMap.pageSize}

select

from goods_category t 
where t.id = #{id,jdbcType=INTEGER}

mapper.update:
update goods_category
name=#{name,jdbcType=VARCHAR}, parent_id=#{parentId,jdbcType=INTEGER}, icon_cls=#{iconCls,jdbcType=VARCHAR}, add_time=#{addTime,jdbcType=TIMESTAMP}, remark=#{remark,jdbcType=VARCHAR},
where 1=1 and id = #{id,jdbcType=INTEGER}
