mapper.select:
select t.id, t.name, t.store, t.freeze_store, t.shelves_time, t.category_id, t.attribute, t.description, t.detail, t.cost, t.market_price, t.status, t.add_user, t.add_time, t.update_time, t.remark, gc.small_pic, gc.big_pic from goods t left join goods_pic gc on t.id=gc.goods_id
order by
${paramMap.orderCol}
id desc
limit #{paramMap.pageOffset},#{paramMap.pageSize}

select

from goods t 
where t.id = #{id,jdbcType=BIGINT}

mapper.update:
update goods
name=#{name,jdbcType=VARCHAR}, store=#{store,jdbcType=INTEGER}, freeze_store=#{freezeStore,jdbcType=INTEGER}, shelves_time=#{shelvesTime,jdbcType=TIMESTAMP}, category_id=#{categoryId,jdbcType=INTEGER}, attribute=#{attribute,jdbcType=VARCHAR}, description=#{description,jdbcType=VARCHAR}, detail=#{detail,jdbcType=LONGVARCHAR}, cost=#{cost,jdbcType=DECIMAL}, market_price=#{marketPrice,jdbcType=DECIMAL}, status=#{status,jdbcType=CHAR}, add_user=#{addUser,jdbcType=VARCHAR}, add_time=#{addTime,jdbcType=TIMESTAMP}, update_time=#{updateTime,jdbcType=TIMESTAMP}, remark=#{remark,jdbcType=VARCHAR},
where 1=1 and id = #{id,jdbcType=BIGINT}
