mapper.update:
update goods_order set
trade_status = #{status,jdbcType=CHAR},
sent_status = #{sentStatus,jdbcType=CHAR},
update_time = NOW()


		id = #{id,jdbcType=INTEGER}


		and order_no = #{orderNo,jdbcType=VARCHAR}

mapper.select:
select

from goods_order t 
where t.order_no = #{orderNo,jdbcType=VARCHAR}
