mapper.select:
select t.id, t.goods_id, t.big_pic, t.small_pic, t.add_time from goods_pic t
order by
${paramMap.orderCol}
id desc
limit #{paramMap.pageOffset},#{paramMap.pageSize}

select

from goods_pic t 
where t.id = #{id,jdbcType=BIGINT}

mapper.update:
update goods_pic set
goods_id = #{goodsId,jdbcType=INTEGER},
big_pic = #{bigPic,jdbcType=VARCHAR},
small_pic = #{smallPic,jdbcType=VARCHAR},
add_time = #{addTime,jdbcType=TIMESTAMP}
where 1=1 and id = #{id,jdbcType=BIGINT}

update goods_pic
goods_id=#{goodsId,jdbcType=INTEGER}, big_pic=#{bigPic,jdbcType=VARCHAR}, small_pic=#{smallPic,jdbcType=VARCHAR}, add_time=#{addTime,jdbcType=TIMESTAMP},
where 1=1 and id = #{id,jdbcType=BIGINT}
