mapper.select:
select t.news_id, t.news_name, t.news_type_id, t.news_author, t.author_profer, t.author_hos, t.author_dept, t.put_on, t.commend, t.create_date, t.photo_address, t.viewed_time, t1.news_type from health_news t LEFT JOIN health_news_type t1 ON t.news_type_id=t1.news_type_id
order by
${paramMap.orderCol}
news_id desc
limit #{paramMap.pageOffset},#{paramMap.pageSize}

mapper.update:
update health_news
news_id=#{newsId,jdbcType=INTEGER}, news_name=#{newsName,jdbcType=VARCHAR}, news_type_id=#{newsTypeId,jdbcType=INTEGER}, news_author=#{newsAuthor,jdbcType=VARCHAR}, author_profer=#{authorProfer,jdbcType=VARCHAR}, author_hos=#{authorHos,jdbcType=VARCHAR}, author_dept=#{authorDept,jdbcType=VARCHAR}, article=#{article,jdbcType=LONGVARCHAR}, put_on=#{putOn,jdbcType=INTEGER}, commend=#{commend,jdbcType=INTEGER}, create_date=#{createDate,jdbcType=TIMESTAMP}, photo_address=#{photoAddress,jdbcType=VARCHAR}, viewed_time=#{viewedTime,jdbcType=INTEGER},
where 1=1 and news_id = #{newsId,jdbcType=INTEGER}
