mapper.select:
SELECT DISTINCT t.hospital_id, t.province, t.city, t.lng, t.lat, t1.hospital_name, t1.hospital_level, (SELECT count(1) FROM hospital_guahao_log t2 WHERE t.hospital_id=t2.hospital_id) mark_num FROM hospital_address t LEFT JOIN hospital_dept t1 ON t.hospital_id = t1.hospital_id
order by
${paramMap.orderCol}
hospital_id desc
limit #{paramMap.pageOffset},#{paramMap.pageSize}

select

from hospital_address t
where t.hospital_id = #{hospitalId,jdbcType=INTEGER}

mapper.update:
update hospital_address
hospital_id=#{hospitalId,jdbcType=INTEGER}, province=#{province,jdbcType=VARCHAR}, city=#{city,jdbcType=VARCHAR}, hospital_name=#{hospitalName,jdbcType=VARCHAR},
where 1=1 and hospital_id = #{hospitalId,jdbcType=INTEGER}
