mapper.select:
select t.dept_id, t.dept_name, t.dept_code, t.hospital_id, t.hospital_name, t.hospital_code, t.hospital_level from hospital_dept t
order by
${paramMap.orderCol}
dept_id desc
limit #{paramMap.pageOffset},#{paramMap.pageSize}

select

from hospital_dept t 
where t.dept_id = #{deptId,jdbcType=INTEGER}

mapper.update:
update hospital_dept
dept_id=#{deptId,jdbcType=INTEGER}, dept_name=#{deptName,jdbcType=VARCHAR}, dept_code=#{deptCode,jdbcType=VARCHAR}, hospital_id=#{hospitalId,jdbcType=INTEGER}, hospital_name=#{hospitalName,jdbcType=VARCHAR}, hospital_code=#{hospitalCode,jdbcType=VARCHAR}, hospital_level=#{hospitalLevel,jdbcType=VARCHAR},
where 1=1 and dept_id = #{deptId,jdbcType=INTEGER}
