mapper.select:
select t.hospital_id, t.hospital_name, t.hospital_code, t.hospital_level, t.province, t.city, t.lng, t.lat, t.appointment_num from hospital t
order by
${paramMap.orderCol}
hospital_id desc
limit #{paramMap.pageOffset},#{paramMap.pageSize}

select

from hospital t
where t.hospital_id = #{hospitalId,jdbcType=INTEGER}

mapper.update:
update hospital
hospital_name=#{hospitalName,jdbcType=VARCHAR}, hospital_code=#{hospitalCode,jdbcType=VARCHAR}, hospital_level=#{hospitalLevel,jdbcType=VARCHAR}, province=#{province,jdbcType=VARCHAR}, city=#{city,jdbcType=VARCHAR}, lng=#{lng,jdbcType=VARCHAR}, lat=#{lat,jdbcType=VARCHAR}, appointment_num=#{appointmentNum,jdbcType=INTEGER},
where hospital_id = #{hospitalId,jdbcType=INTEGER}
