mapper.select:
select t.doctor_license, t.doctor_name, t.doctor_code, t.dept_id, t.dept_code, t.field, t.doctor_intro, t.positional from hospital_register_doctor t
order by
${paramMap.orderCol}
doctor_code desc
limit #{paramMap.pageOffset},#{paramMap.pageSize}

select

from hospital_register_doctor t
where t.doctor_code = #{doctorCode,jdbcType=VARCHAR}

mapper.update:
update hospital_register_doctor
doctor_license=#{doctorLicense,jdbcType=INTEGER}, doctor_name=#{doctorName,jdbcType=VARCHAR}, doctor_code=#{doctorCode,jdbcType=VARCHAR}, dept_id=#{deptId,jdbcType=INTEGER}, dept_code=#{deptCode,jdbcType=VARCHAR}, field=#{field,jdbcType=VARCHAR}, doctor_intro=#{doctorIntro,jdbcType=LONGVARCHAR}, positional=#{positional,jdbcType=VARCHAR},
where 1=1 and doctor_code = #{doctorCode,jdbcType=VARCHAR}
