mapper.select:
select t.user_id, t.doctor_id, t.add_time from my_doctor t
order by
${paramMap.orderCol}
doctor_id,user_id desc
limit #{paramMap.pageOffset},#{paramMap.pageSize}

select

from my_doctor t where 1=1
and doctor_id=#{doctorId,jdbcType=VARCHAR}
and user_id=#{userId,jdbcType=VARCHAR}

mapper.delete:
delete from my_doctor where 1=1
and doctor_id=#{doctorId,jdbcType=VARCHAR}
and user_id=#{userId,jdbcType=VARCHAR}

mapper.insert:
insert into my_doctor (
user_id,
doctor_id,
add_time
)
values(
#{userId,jdbcType=VARCHAR},
#{doctorId,jdbcType=VARCHAR},
#{addTime,jdbcType=TIMESTAMP}
)

mapper.update:
update my_doctor
user_id=#{userId,jdbcType=VARCHAR}, doctor_id=#{doctorId,jdbcType=VARCHAR}, add_time=#{addTime,jdbcType=TIMESTAMP},
where 1=1 and doctor_id = #{doctorId,jdbcType=VARCHAR} and user_id = #{userId,jdbcType=VARCHAR}
