mapper.select:
SELECT t.id, t.notice_type_id, t.type, t.user_id, t.content, t.add_time, t.last_update_dtime, CASE WHEN before_time_str BETWEEN 3 AND 59 THEN CONCAT(before_time_str,'分钟前') WHEN before_time_str BETWEEN 60 AND 1440 THEN CONCAT(CEIL(before_time_str/60),'小时前') WHEN before_time_str > 1440 THEN CONCAT(CEIL(before_time_str/1440),'天前') ELSE '刚刚' END AS before_time_str FROM ( SELECT t.id, t.notice_type_id, t1.type, t.user_id, t.content, t.add_time, t.last_update_dtime, TIMESTAMPDIFF(MINUTE,t.last_update_dtime,NOW()) before_time_str FROM notice_message t LEFT JOIN notice_type t1 ON t.notice_type_id = t1.id ) t
order by
${paramMap.orderCol}
id desc
limit #{paramMap.pageOffset},#{paramMap.pageSize}

select

from notice_message t
where t.id = #{id,jdbcType=INTEGER}

mapper.update:
update notice_message
notice_type_id=#{noticeTypeId,jdbcType=VARCHAR}, user_id=#{userId,jdbcType=VARCHAR}, content=#{content,jdbcType=LONGVARCHAR}, add_time=#{addTime,jdbcType=TIMESTAMP}, last_update_dtime=#{lastUpdateDtime,jdbcType=TIMESTAMP},
where 1=1 and id = #{id,jdbcType=INTEGER}
