mapper.select:
select t.id, t.patient_record_id, t.diag_code, t.diag_name, t.diag_date, t.diag_type_code, t.last_update_dtime from patient_diag t
order by
${paramMap.orderCol}
id desc
limit #{paramMap.pageOffset},#{paramMap.pageSize}

select

from patient_diag t 
where t.id = #{id,jdbcType=BIGINT}

mapper.update:
update patient_diag
patient_record_id=#{patientRecordId,jdbcType=INTEGER}, diag_code=#{diagCode,jdbcType=VARCHAR}, diag_name=#{diagName,jdbcType=VARCHAR}, diag_date=#{diagDate,jdbcType=TIMESTAMP}, diag_type_code=#{diagTypeCode,jdbcType=VARCHAR}, last_update_dtime=#{lastUpdateDtime,jdbcType=TIMESTAMP},
where 1=1 and id = #{id,jdbcType=BIGINT}
