mapper.select:
select t.id, t.patient_diag_id, t.drug_name, t.drug_std_name, t.drug_std_code, t.notes, t.last_update_dtime from patient_drug t
order by
${paramMap.orderCol}
id desc
limit #{paramMap.pageOffset},#{paramMap.pageSize}

select

from patient_drug t 
where t.id = #{id,jdbcType=BIGINT}

mapper.update:
update patient_drug
patient_diag_id=#{patientDiagId,jdbcType=INTEGER}, drug_name=#{drugName,jdbcType=VARCHAR}, drug_std_name=#{drugStdName,jdbcType=VARCHAR}, drug_std_code=#{drugStdCode,jdbcType=VARCHAR}, notes=#{notes,jdbcType=VARCHAR}, last_update_dtime=#{lastUpdateDtime,jdbcType=TIMESTAMP},
where 1=1 and id = #{id,jdbcType=BIGINT}
