mapper.select:
select

from upload_content t
where pub_id=#{pubId,jdbcType=VARCHAR}

mapper.update:
update upload_content
pub_id=#{pubId,jdbcType=VARCHAR}, user_id=#{userId,jdbcType=INTEGER}, type=#{type,jdbcType=INTEGER}, content=#{content,jdbcType=VARCHAR}, src=#{src,jdbcType=VARCHAR}, pic=#{pic,jdbcType=VARCHAR}, price=#{price,jdbcType=DECIMAL}, remark=#{remark,jdbcType=VARCHAR}, add_time=#{addTime,jdbcType=TIMESTAMP}, last_update_dtime=#{lastUpdateDtime,jdbcType=TIMESTAMP},
where pub_id=#{pubId,jdbcType=VARCHAR}
