mapper.select:
SELECT * FROM ( SELECT t.id, t.message_id, t.question_id, t.user_id, t1.to_user, t.content, t.src, t.remark, t.add_time, t1.question, t1.answer, u1.real_name AS patientRealName, u2.real_name AS doctorRealName FROM upload_patient t LEFT JOIN ask_question_answer t1 ON t1.id = t.question_id LEFT JOIN user u1 ON u1.user_id=t.user_id LEFT JOIN user u2 ON u2.user_id=t1.to_user ) t
order by
${paramMap.orderCol}
id desc
limit #{paramMap.pageOffset},#{paramMap.pageSize}

select

from upload_patient t
where t.id = #{id,jdbcType=INTEGER}

mapper.update:
update upload_patient
message_id=#{messageId,jdbcType=VARCHAR}, question_id=#{questionId,jdbcType=INTEGER}, user_id=#{questionId,jdbcType=INTEGER}, content=#{content,jdbcType=VARCHAR}, src=#{src,jdbcType=VARCHAR}, remark=#{remark,jdbcType=VARCHAR}, add_time=#{addTime,jdbcType=TIMESTAMP},
where id = #{id,jdbcType=INTEGER}
