mapper.select:
select

from upload_patient_record t
where t.user_id=#{userId,jdbcType=INTEGER}

mapper.update:
update upload_patient_record
user_id=#{userId,jdbcType=INTEGER}, title=#{title,jdbcType=VARCHAR}, content=#{content,jdbcType=VARCHAR}, src=#{src,jdbcType=VARCHAR}, remark=#{remark,jdbcType=VARCHAR}, add_time=#{addTime,jdbcType=TIMESTAMP}, last_update_dtime=#{lastUpdateDtime,jdbcType=TIMESTAMP},
where user_id=#{userId,jdbcType=INTEGER}
